#Algorithm used : Ford-Fulkerson Algorithm for Maximum Flow
#Author : shubham vashisht
from collections import defaultdict
def BFS(graph,s, t, parent):
    ROW=len(graph)
    COL=len(graph[0])

    visited =[False]*(ROW)
    queue=[]

    queue.append(s)
    visited[s] = True
     
    while queue:

        
        u = queue.pop(0)

        for ind, val in enumerate(graph[u]):
            if visited[ind] == False and val > 0 :
                queue.append(ind)
                visited[ind] = True
                parent[ind] = u
    return True if visited[t] else False
         
 
def FordFulkerson(graph, source, sink):
    ROW=len(graph)
    COL=len(graph[0])
    parent = [-1]*(ROW)

    max_flow = 0
    while BFS(graph,source, sink, parent) :
        path_flow = float("Inf")
        s = sink
        while(s !=  source):
            path_flow = min (path_flow, graph[parent[s]][s])
            s = parent[s]

        max_flow +=  path_flow
        v = sink
        while(v !=  source):
            u = parent[v]
            graph[u][v] -= path_flow
            graph[v][u] += path_flow
            v = parent[v]

    return max_flow

def answer(entrances, exits, path):
    res=0
    for i in range(len(exits)):
        res+=FordFulkerson(path,entrances[i], exits[i-1])
    return res
